def organiza_esquerda(elem, array):
    # Move o elemento na posição 'elem' para a esquerda
    for j in range(elem, 0, -1):
        array[j], array[j-1] = array[j-1], array[j] 

def organiza_direita(elem, array):
    # Move o elemento na posição 'elem' para a direita
    for j in range(elem, len(array) - 1):
        array[j], array[j+1] = array[j+1], array[j]

def reorganiza_dms_k(array, k):
    n = len(array)
    
    # Primeiro move os elementos com DMS igual a k para a esquerda
    for i in range(n):
        if array[i] % 10 == k:
            organiza_esquerda(i, array)
            # Depois de mover, a posição 'i' pode ter mudado
            i -= 1  # Decrementa i para evitar pular um elemento
    
    # Em seguida, move os elementos com DMS igual a k+1 para a direita
    for i in range(n - 1, -1, -1):
        if array[i] % 10 == (k + 1) % 10:  # Tratar 9 como 0
            organiza_direita(i, array)

# Exemplo de uso
numeros = [123, 411, 875, 492, 623, 881, 903]
reorganiza_dms_k(numeros, 2)
assert numeros == [492, 411, 875, 881, 903, 623, 123]


